import './Manager.css';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import { useEffect, useRef } from 'react';
import { useSelector , useDispatch} from 'react-redux';
import { getReports , deleteReport } from '../../DataCenter/DataCenter';


function ManagerPage() {
    const reports = useSelector(state=>state.reports.reports);
    const isLoading = useSelector(state=>state.reports.isLoading);
    const dispatch = useDispatch();
    const searchBar = useRef();

console.log(isLoading)

    const handleDelete = (id) =>{
        dispatch(deleteReport(id));
    }
    useEffect(()=>{
        dispatch(getReports())
    },[dispatch])
    useEffect(()=>{
        dispatch(getReports())
    },[isL])




    const handleSearch = () => {
        const li = document.querySelectorAll('li');
        for(let i = 0; i < li.length ; i++) {
            if(li[i].firstElementChild.innerText.toUpperCase().indexOf(searchBar.current.value.toUpperCase())){
                li[i].style.display = 'none';
            } else {
                li[i].style.display = 'block';
            }
        }
    }

    return ( 
        <section>
            <div className='dataList'>
                <input type='text' ref={searchBar} onChange={()=>{handleSearch()}}/>
                <ul>
                    {reports == null ?'loading' : reports.map(report=>
                    <li key={report.id}>
                        <p>{report.address}</p>
                        <Stack direction="row" spacing={2}>
                        <Button variant="contained" color="success">
                            READE
                        </Button>
                        <Button color="secondary">
                            UPDATE
                        </Button>
                        <Button variant="outlined" color="error" onClick={()=>{handleDelete(report.id)}}>
                            DELETE
                        </Button>
                        </Stack>
                    </li>)}
                </ul>
            </div>
        </section>
     );
}

export default ManagerPage;
