import { Formik, Field, Form } from 'formik';
import Button from '@mui/material/Button';
import SendIcon from '@mui/icons-material/Send';
import DeleteIcon from '@mui/icons-material/Delete';
import { Row, Col } from 'react-bootstrap';
import defaultUserImg from '../Assets/Images/illustration-of-human-icon-user-symbol-icon-modern-design-on-blank-background-free-vector.webp';


function FormComponent() {
  const handleSubmit = (values) => {
    console.log(values);
  };

  const handleUserImg = (e, setFieldValue) => {
    const file = e.target.files[0];
    const imgUrl = URL.createObjectURL(file);
    setFieldValue('userImg', imgUrl);
    console.log(imgUrl);
  };

  return (
    <>
      <Formik
        initialValues={{
          firstName: '',
          lastName: '',
          email: '',
          password: '',
          userImg: '',
        }}
        onSubmit={handleSubmit}
      >
        {({ setFieldValue }) => (
          <Form>
            <Row>
              <Field id='firstName' name='firstName' placeholder='First Name' />

              <Field id='lastName' name='lastName' placeholder='Last Name' />

              <Field id='email' name='email' placeholder='E-mail' type='email' />

              <Field id='password' name='password' placeholder='Password' type='password' />

              <Col className='userImgForm'>
                <img src={defaultUserImg} className='defaultUserImg' />
                <input
                  id='userImg'
                  name='userImg'
                  type='file'
                  accept='image/jpeg, image/png, image/jpg'
                  onChange={(e) => handleUserImg(e, setFieldValue)}
                />
              </Col>

              <div className='formButtons'>
                <Button className='formBtn' type='submit' variant='contained' endIcon={<SendIcon />}>
                  Send
                </Button>
                <Button className='formBtn' variant='outlined' startIcon={<DeleteIcon />}>
                  Delete
                </Button>
              </div>
            </Row>
          </Form>
        )}
      </Formik>
    </>
  );
}

export default FormComponent;
