import { useState } from "react";
import { Field, connect } from "formik";
import defaultUserImg from "../Assets/Images/illustration-of-human-icon-user-symbol-icon-modern-design-on-blank-background-free-vector.webp";

function UserImg({ formik }) {
  const { setFieldValue } = formik;
  const [userPic, setUserPic] = useState("");

  const handleUserImg = (e) => {
    const file = e.target.files[0];
    setFieldValue("userImg", file);
    console.log(file);
  };

  return (
    <>
      <img src={defaultUserImg} className="defaultUserImg" />
      <Field name="userImg">
        {() => (
          <input
            id="userImg"
            name="userImg"
            type="file"
            accept="image/jpeg, image/png, image/jpg"
            onChange={handleUserImg}
          />
        )}
      </Field>
    </>
  );
}

export default connect(UserImg);
