import { Formik, Field, Form } from 'formik';
import Button from '@mui/material/Button';
import SendIcon from '@mui/icons-material/Send';
import DeleteIcon from '@mui/icons-material/Delete';
import {Row , Col} from 'react-bootstrap';

function FormComponent() {
    const userImg = useRef(null);

    const handleSubmit = (values) => {
        console.log(values)
    }
    const handleUserImg = () => {
        const getUrl = URL.createObjectURL(userImg.current.files[0])

        console.log(userImg.current)
    }

  return (
    <>
        <Formik initialValues={{
            firstName:'',
            lastName:'',
            email:'',
            password: '',
            userImg: '',
        }}
        onSubmit={handleSubmit}
        >
        <Form >
            <Row>
                <Field id='firstName' name='firstName' placeholder='First Name'/>
               
                <Field id="lastName" name="lastName" placeholder="Last Name" />
                
                <Field id="email" name="email" placeholder="E-mail" type='email' />
             
                <Field id="password" name="password" placeholder="Password" type='password' />
                
                <Field ref={userImg} id="userImg" name="userImg" placeholder='Chose Picture' type='file' accept='image/jpeg, image/png, image/jpg' onChange={()=>handleUserImg()}/>
            <div className='formButtons'>
                <Button className='formBtn' type='submit' variant="contained" endIcon={<SendIcon />}>
                    Send
                </Button>
                <Button className='formBtn' variant="outlined" startIcon={<DeleteIcon />}>
                    Delete
                </Button>
            </div>
            </Row>
        </Form>
        </Formik>
    </>
  );
}

export default FormComponent;
